  NavLink,
  TabContent,
  TabPane,
  Table,
  Input,
  Form,
  FormGroup,
  Button
} from 'reactstrap';
import InputMask from 'react-input-mask';
import classnames from 'classnames';
import List from './List';
import Form1 from './Form1';
import Form2 from './Form2';

class Forms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      form: 'form1',
      form1data: {
        dept_no: null,
        instructor_sector_no: null,
        counting_sector_no: null,
        resident_list_no: null,
        permanent_resident_household_count: null,
        total_resident_person_count: null,
        permanent_resident_person_count: null,
        temporary_nonresident_person_count: null,
        temporary_resident_person_count: null,
        longterm_nonresident_person_count: null,
        visitor_person_count: null,
        srs_eni_code: null,
        nsc_eni_code: null,
        region: null,
        district: null,
        town: null,
        city_district: null,
        township: null,
        microdistrict: null,
        settlment: null,
        village: null,
        street: null,
        house: null,
        flat: null,
        surname: null,
        organization_name: null,
      },
      form1persons: [],
      activeTab: 'form1',
      dept_no: null,
      instructor_sector_no: null,
      counting_sector_no: null,
      sheet: null,
      briefcase: null,
      nsc_soate_code: null,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tabtoggle = this.tabtoggle.bind(this);
  }

  componentDidMount() {
    document.getElementById('dept_no').focus();
    document.getElementById('dept_no').select();
  }

  onKeyPressed(e) {
    if (e.keyCode === 13 || e.keyCode === 39 || e.keyCode === 40) {
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (index <= form.elements.length - 1) {
        form.elements[index + 1].focus();
        form.elements[index + 1].select();
      }
      e.preventDefault();
    }
    if (e.keyCode === 37 || e.keyCode === 38) {
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (index > 0) {
        form.elements[index - 1].focus();
        form.elements[index - 1].select();
      }
      e.preventDefault();
    }
    if (e.keyCode === 107) {
      console.log(e.keyCode);
      e.preventDefault();
    }
  }

  handleChange(event) {
    const name = event.target.name;
    this.setState({ [name]: event.target.value });
    if (name === "dept_no") {
      let f1data = this.state.form1data
      f1data.dept_no = event.target.value
      this.setState({ formdata: f1data })
    }
    if (name === "instructor_sector_no") {
      let f1data = this.state.form1data
      f1data.instructor_sector_no = event.target.value
      this.setState({ formdata: f1data })
    }
    if (name === "counting_sector_no") {
      let f1data = this.state.form1data
      f1data.counting_sector_no = event.target.value
      this.setState({ formdata: f1data })
    }
    if (name === "nsc_soate_code") {
      let f1data = this.state.form1data
      f1data.nsc_eni_code = event.target.value
      this.setState({ formdata: f1data })
    }
    console.log(this.state)
  }

  handleSubmit() {
    console.log(this.state.activeTab)
    this.submitForm1()
  }

  tabtoggle(tab) {
    console.log(this.state.activeTab)
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  async submitForm1(event) {
    console.log(this.state.form1data)
    let insertid = 0
    // event.preventDefault();
    await fetch(`http://192.168.2.103:8000/form_1`, {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      method: "POST",
      body: JSON.stringify(this.state.form1data)
    })
      .then(response => response.json())
      .then(data => {
        console.log(data)
        insertid = data.data.insertId
      })
      .catch(error => console.error(error))
    const f13data = {
      dept_no: this.state.dept_no,
      instructor_sector_no: this.state.instructor_sector_no,
      counting_sector_no: this.state.counting_sector_no,
      sheet: this.state.sheet,
      briefcase: this.state.briefcase,
      nsc_soate_code: this.state.nsc_soate_code,
      form_number: 1,
      form_id: insertid
    }
    if (insertid !== 0) {
      await fetch(`http://192.168.2.103:8000/forms`, {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        method: "POST",
        body: JSON.stringify(f13data)
      })
        .then(response => response.json())
        .then(data => {
          console.log(data)
        })
        .catch(error => console.error(error))
    }
    // this.cleardataForm1()
    this.tabtoggle('3')
  }

  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

  render() {

    return (
      <div className="animated fadeIn">
        <Row>
          <Col>
            <Card onKeyDown={(e) => this.onKeyPressed(e)} >
              <CardHeader>Ввод данных</CardHeader>
              <CardBody>
                <Form id="forms">
                  <Row>
                    <Col md={4}>
                      <Table size="sm" bordered responsive>
                        <thead>
                          <tr>
                            <th rowSpan="2">№ переписного отдела</th>
                            <th rowSpan="2">№ инструкторского участка</th>
                            <th rowSpan="2">№ счетного участка</th>
                          </tr>
                        </thead>
                        <tbody>
                          <tr>
                            <td>
                              <Input
                                onChange={(e) => this.handleChange(e)}
                                bsSize="sm"
                                type="text"
                                mask="999"
                                maskChar=""
                                tag={InputMask}
                                name="dept_no"
                                id="dept_no"
                              />
                            </td>
                            <td>
                              <Input
                                onChange={(e) => this.handleChange(e)}
                                bsSize="sm"
                                type="text"
                                mask="999"
                                maskChar=""
                                tag={InputMask}
                                name="instructor_sector_no"
                                id="instructor_sector_no"
                              />
                            </td>
                            <td>
                              <Input
                                onChange={(e) => this.handleChange(e)}
                                bsSize="sm"
                                type="text"
                                mask="99"
                                maskChar=""
                                tag={InputMask}
                                name="counting_sector_no"
                                id="counting_sector_no"
                              />
                            </td>
                          </tr>
                        </tbody>
                      </Table>
                    </Col>
                    <Col md={4}>
                      <Table bordered>
                        <tbody>
                          <tr>
                            <th style={{ width: "80%" }}>
                              Сопроводительный лист №
                          </th>
                            <td style={{ width: "20%" }}>
                              <Input
                                onChange={(e) => this.handleChange(e)}
                                bsSize="sm"
                                type="text"
                                mask="999"
                                maskChar=""
                                tag={InputMask}
                                name="sheet"
                              />
                            </td>
                          </tr>
                        </tbody>
                      </Table>
                    </Col>
                    <Col md={4}>
                      <Table bordered>
                        <tbody>
                          <tr>
                            <th style={{ width: "80%" }}>
                              № портфеля
                          </th>
                            <td style={{ width: "20%" }}>
                              <Input
                                onChange={(e) => this.handleChange(e)}
                                bsSize="sm"
                                type="text"
                                mask="999"
                                maskChar=""
                                tag={InputMask}
                                name="briefcase"
                              />
                            </td>
                          </tr>
                        </tbody>
                      </Table>
                    </Col>
                  </Row>
                  <Row>
                    <Col md={12}>
                      <Table bordered>
                        <tbody>
                          <tr>
                            <th style={{ width: "20%" }}>
                              Код ГК СОАТЕ
                                </th>
                            <td style={{ width: "20%" }}>
                              <Input
                                onChange={(e) => this.handleChange(e)}
                                bsSize="sm"
                                type="text"
                                mask="99999999999999"
                                maskChar=""
                                tag={InputMask}
                                name="nsc_soate_code"
                              />
                            </td>
                            <td style={{ width: "60%" }}>
                            </td>
                          </tr>
                        </tbody>
                      </Table>
                    </Col>
                  </Row>

                  <Nav tabs key="child">
                    <NavItem>
                      <NavLink
                        className={classnames({ active: this.state.activeTab === 'form1' })}
                        onClick={() => { this.tabtoggle('form1'); }}
                      >
                        Форма 1
                    </NavLink>
                    </NavItem>
                    <NavItem>
                      <NavLink
                        className={classnames({ active: this.state.activeTab === 'form2' })}
                        onClick={() => { this.tabtoggle('form2'); }}
                      >
                        Форма 2
                    </NavLink>
                    </NavItem>
                    <NavItem>
                      <NavLink
                        className={classnames({ active: this.state.activeTab === 'form3' })}
                        onClick={() => { this.tabtoggle('form3'); }}
                      >
                        Форма 3
                    </NavLink>
                    </NavItem>
                    <NavItem>
                      <NavLink
                        className={classnames({ active: this.state.activeTab === 'form4' })}
                        onClick={() => { this.tabtoggle('form4'); }}
                      >
                        Форма 4
                    </NavLink>
                    </NavItem>
                    <NavItem>
                      <NavLink
                        className={classnames({ active: this.state.activeTab === 'form5' })}
                        onClick={() => { this.tabtoggle('form5'); }}
                      >
                        Форма 5
                    </NavLink>
                    </NavItem>
                    <NavItem>
                      <NavLink
                        className={classnames({ active: this.state.activeTab === 'list' })}
                        onClick={() => { this.tabtoggle('list'); }}
                      >
                        Список
                    </NavLink>
                    </NavItem>
                  </Nav>
                  <TabContent activeTab={this.state.activeTab}>
                    <TabPane tabId="form1">
                      <Form1 formdata={this.state.form1data} persons={this.state.form1persons} />
                    </TabPane>
                    <TabPane tabId="form2">
                      <Form2 />
                    </TabPane>
                    <TabPane tabId="list">
                      <List />
                    </TabPane>
                  </TabContent>
                </Form>
              </CardBody>
              <CardFooter>
                <FormGroup>
                  {this.state.activeTab !== 'list' ? <Button onClick={this.handleSubmit} color="primary" size="sm">Сохранить</Button> : null}
                </FormGroup>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Forms;
