import React, { Component } from 'react';
import {
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Row,
  Nav,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
  Table,
  Input,
  Form,
  FormGroup,
  Button
} from 'reactstrap';
import InputMask from 'react-input-mask';
import classnames from 'classnames';
import List from './List';
import Form1 from './Form1';
import Form2 from './Form2';

class Forms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      form: 'form1',
      form1data: {
        dept_no: '',
        instructor_sector_no: '',
        counting_sector_no: '',
        resident_list_no: '',
        permanent_resident_household_count: '',
        total_resident_person_count: '',
        permanent_resident_person_count: '',
        temporary_nonresident_person_count: '',
        temporary_resident_person_count: '',
        longterm_nonresident_person_count: '',
        visitor_person_count: '',
        enigrssrs_eni_code: '',
        nsc_eni_code: '',
        region: '',
        district: '',
        town: '',
        city_district: '',
        township: '',
        microdistrict: '',
        settlment: '',
        village: '',
        street: '',
        house: '',
        flat: '',
        name: '',
        organization: '',
      },
      form2data: [],
      form3data: [],
      form4data: [],
      form5data: [],
      value: '',
      activeTab: 'form1',
      f13deptno: '',
      f13insno: '',
      f13cosno: '',
      listnumber: '',
      brcnumber: '',
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tabtoggle = this.tabtoggle.bind(this);
  }

  componentDidMount() {
    document.getElementById('f_dept_no').focus();
    document.getElementById('f_dept_no').select();
  }

  onKeyPressed(e) {
    if (e.keyCode === 13 || e.keyCode === 39 || e.keyCode === 40) {
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (index <= form.elements.length - 1) {
        form.elements[index + 1].focus();
        form.elements[index + 1].select();
      }
      e.preventDefault();
    }
    if (e.keyCode === 37 || e.keyCode === 38) {
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (index > 0) {
        form.elements[index - 1].focus();
        form.elements[index - 1].select();
      }
      e.preventDefault();
    }
    if (e.keyCode === 107) {
      console.log(e.keyCode);
      e.preventDefault();
    }
  }

