import React, { Component } from 'react';
import {
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Row,
  Nav,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
  Table,
  Input,
  Form,
  FormGroup,
  Button
} from 'reactstrap';
import classnames from 'classnames';
import List from './List';
import Form1 from './Form1';
import Form2 from './Form2';

class Forms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      form: 'form1',
      value: '',
      activeTab: 'form1',
      h131: '',
      h132: '',
      h133: '',
      listnumber: '',
      portnumber: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tabtoggle = this.tabtoggle.bind(this);
  }

  componentDidMount() {
    document.getElementById('h131').focus();
    // console.log(formru)
  }

  onKeyPressed(e) {
    if (e.keyCode === 13) {
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (form.id === 'forms' && index === 4) {
        document.getElementById('h1').focus();
      } else {
        form.elements[index + 1].focus();
      }
      e.preventDefault();
    }
    if (e.keyCode === 107) {
      console.log(e.keyCode);
      e.preventDefault();
    }
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
    console.log(event.target.value)
  }

  handleSubmit() {

  }

  tabtoggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
      console.log(this.state.activeTab)
    }
  }

  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

  render() {

    return (
      <div className="animated fadeIn">
        <Row>
          <Col>
            <Card onKeyDown={(e) => this.onKeyPressed(e)} >
              <CardHeader>Ввод данных</CardHeader>
              <CardBody>
                <Form id="forms">
                  <Row>
                    <Col md={4}>
                      <Table size="sm" bordered responsive>
                        <thead>
                          <tr>
                            <th rowSpan="2">№ переписного отдела</th>
                            <th rowSpan="2">№ инструкторского участка</th>
                            <th rowSpan="2">№ счетного участка</th>
                          </tr>
                        </thead>
                        <tbody>
                          <tr>
                            <td><Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="h131" id="h131" /></td>
                            <td><Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="h123" id="h131" /></td>
                            <td><Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="h133" id="h131" /></td>
                          </tr>
                        </tbody>
                      </Table>
                    </Col>
                    <Col md={4}>
                      <Table bordered>
                        <tbody>
                          <tr>
                            <th style={{ width: "80%" }}>
                              Сопроводительный лист №
                          </th>
                            <td style={{ width: "20%" }}>
                              <Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="listnumber" />
                            </td>
                          </tr>
                        </tbody>
                      </Table>
                    </Col>
                    <Col md={4}>
                      <Table bordered>
                        <tbody>
                          <tr>
                            <th style={{ width: "80%" }}>
                              № портфеля
                          </th>
                            <td style={{ width: "20%" }}>
                              <Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="portnumber" />
                            </td>
                          </tr>
                        </tbody>
                      </Table>
                    </Col>
                  </Row>
                </Form>

                <Nav tabs key="child">
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === 'form1' })}
                      onClick={() => { this.tabtoggle('form1'); }}
                    >
                      Форма 1
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === 'form2' })}
                      onClick={() => { this.tabtoggle('form2'); }}
                    >
                      Форма 2
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === 'form3' })}
                      onClick={() => { this.tabtoggle('form3'); }}
                    >
                      Форма 3
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === 'form4' })}
                      onClick={() => { this.tabtoggle('form4'); }}
                    >
                      Форма 4
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === 'form5' })}
                      onClick={() => { this.tabtoggle('form5'); }}
                    >
                      Форма 5
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === 'list' })}
                      onClick={() => { this.tabtoggle('list'); }}
                    >
                      Список
                    </NavLink>
                  </NavItem>
                </Nav>
                <TabContent activeTab={this.state.activeTab}>
                  <TabPane tabId="list">
                    <List />
                  </TabPane>
                  <TabPane tabId="form1">
                    <Form1 />
                  </TabPane>
                  <TabPane tabId="form2">
                    <Form2 />
                  </TabPane>
                </TabContent>

              </CardBody>
              <CardFooter>
                <FormGroup>
                  { this.state.activeTab !== 'list' ? <Button onClick={this.handleSubmit} color="primary" size="sm">Сохранить</Button> : null }
                </FormGroup>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Forms;
