import React, { Component } from 'react';
import {
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Row,
  Nav,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
  Table,
  Input,
  FormGroup,
  Label
} from 'reactstrap';
import classnames from 'classnames';
import List from './List';
import Form1 from './Form1';
import Form2 from './Form2';

class Forms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      activeTab: '1'
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tabtoggle = this.tabtoggle.bind(this);
  }

  componentDidMount() {
    document.getElementById('h1').focus();
    // console.log(formru)
  }

  onKeyPressed(e) {
    if (e.keyCode === 13) {
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      form.elements[index + 1].focus();
      e.preventDefault();
    }
    if (e.keyCode === 107) {
      console.log(e.keyCode);
      e.preventDefault();
    }
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
    console.log(event.target.value)
  }

  handleSubmit() {

  }

  tabtoggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

  render() {

    return (
      <div className="animated fadeIn">
        <Row>
          <Col>
            <Card onKeyDown={(e) => this.onKeyPressed(e)} >
              <CardHeader>Ввод данных</CardHeader>
              <CardBody>
                
                <Table size="sm" bordered responsive>
                  <thead>
                    <tr>
                      <th rowSpan="2">№ переписного отдела</th>
                      <th rowSpan="2">№ инструкторского участка</th>
                      <th rowSpan="2">№ счетного участка</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td><Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="h1" id="h1" /></td>
                      <td><Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="h2" id="h2" /></td>
                      <td><Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="h3" id="h3" /></td>
                    </tr>
                  </tbody>
                </Table>
                <Row form>
                  <Col md={4}>
                      <FormGroup>
                          <Label style={{ fontWeight: 'bold' }}>Сопроводительный лист №</Label>
                          <Input onChange={(e) => this.handleChange(e)} bsSize="sm" type="text" name="relation" id="relation" />
                      </FormGroup>
                  </Col>
                </Row>
                <Nav tabs key="child">
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === '1' })}
                      onClick={() => { this.tabtoggle('1'); }}
                    >
                      Список
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === '3' })}
                      onClick={() => { this.tabtoggle('3'); }}
                    >
                      Форма 1
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      // disabled
                      className={classnames({ active: this.state.activeTab === '4' })}
                      onClick={() => { this.tabtoggle('4'); }}
                    >
                      Форма 2
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      className={classnames({ active: this.state.activeTab === '5' })}
                      onClick={() => { this.tabtoggle('5'); }}
                    >
                      Форма 3
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      className={classnames({ active: this.state.activeTab === '6' })}
                      onClick={() => { this.tabtoggle('6'); }}
                    >
                      Форма 4
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      className={classnames({ active: this.state.activeTab === '7' })}
                      onClick={() => { this.tabtoggle('7'); }}
                    >
                      Форма 5
                    </NavLink>
                  </NavItem>
                </Nav>
                <TabContent activeTab={this.state.activeTab}>
                  <TabPane tabId="1">
                    <List />
                  </TabPane>
                  <TabPane tabId="3">
                    <Form1 />
                  </TabPane>
                  <TabPane tabId="4">
                    <Form2 />
                  </TabPane>
                </TabContent>

              </CardBody>
              <CardFooter>

              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Forms;
