import React, { Component } from 'react';
import {
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Row,
  Nav,
  NavItem,
  NavLink,
  TabContent,
  TabPane,
  Table,
  Input,
  Form,
  FormGroup,
  Button
} from 'reactstrap';
import classnames from 'classnames';
import List from './List';
import Form1 from './Form1';
import Form2 from './Form2';

class Forms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      form: 'form1',
      form1data:[],
      form1person:[],
      form2data:[],
      form3data:[],
      form4data:[],
      form5data:[],
      value: '',
      activeTab: 'form1',
      f13deptno: '',
      f13insno: '',
      f13cosno: '',
      listnumber: '',
      brcnumber: ''
    };
    this.handleChange = this.handleChange.bind(this);
    // this.handleSubmitForm1 = this.handleSubmitForm1.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tabtoggle = this.tabtoggle.bind(this);
    // this.cleardataForm1 = this.cleardataForm1.bind(this);
  }

  componentDidMount() {
    document.getElementById('h131').focus();
    document.getElementById('h131').select();
    // console.log(formru)
  }

  onKeyPressed(e) {
    if (e.keyCode === 13 || e.keyCode === 39 || e.keyCode === 40) {
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (form.id === 'forms' && index === 4) {
        document.getElementById('h1').focus();
      } else {
        form.elements[index + 1].focus();
        form.elements[index + 1].select();
      }
      e.preventDefault();
    }
    if (e.keyCode === 37 || e.keyCode === 38) {
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      form.elements[index - 1].focus();
      form.elements[index - 1].select();
      e.preventDefault();
    }
    if (e.keyCode === 107) {
      console.log(e.keyCode);
      e.preventDefault();
    }
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
    console.log(event.target.value)
  }

  handleSubmit() {
    console.log(this.state.activeTab)
    this.submitForm1()
  }

  tabtoggle(tab) {
    console.log(this.state.activeTab)
    if (this.state.activeTab !== tab) {
